mapper.select:
 	select bno,title,content, writer,regdate,viewcnt,loginUser,writerId
	from tbl_board
	where bno=#{bno}

    	select COUNT(*) 
		from tbl_board
		where bno=#{bno}

    	select COUNT(*) 
		from tbl_reply
		where bno=#{rbi}

mapper.update:
UPDATE MP_FILE SET
BNO = #{BNO}
WHERE FILE_NO = #{FILE_NO} and bno = null

UPDATE MP_FILE SET
DEL_GB = 'Y'
WHERE FILE_NO = #{FILE_NO}
